package com.mzbr.videoeditingservice.util;import java.io.File;import java.io.FileInputStream;import java.io.IOException;import java.net.URI;import java.nio.file.Files;import java.nio.file.Path;import java.nio.file.Paths;import java.util.List;import org.springframework.beans.factory.annotation.Value;import org.springframework.stereotype.Component;import lombok.RequiredArgsConstructor;import software.amazon.awssdk.core.sync.RequestBody;import software.amazon.awssdk.services.s3.S3Client;import software.amazon.awssdk.services.s3.model.PutObjectRequest;@Component@RequiredArgsConstructorpublic class S3Util {	private final S3Client s3Client;	@Value("${cloud.aws.s3.bucket}")	private String BUCKET_NAME;	public void uploadLocalFileByStringFormat(List<Path> pathList) {		for (Path path : pathList) {			PutObjectRequest putObjectRequest = PutObjectRequest.builder()				.bucket(BUCKET_NAME)				.key(path.getFileName().toString())				.build();			s3Client.putObject(putObjectRequest, RequestBody.fromFile(path.toFile()));		}	}}